mapper.select:
select id,consignee,sex,phone,province_code as provinceCode,province_name as provinceName,city_code as cityCode,city_name as cityName,
       district_code as districtCode,district_name as districtName,detail,label,is_default as isDefault from address_book
where user_id = #{id}

